
public class String_In_Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="Cricket";
		
		switch(s) {
			case "Tennis":
				System.out.println("We are playing tennis");
				break;
			
			case "Cricket":
				System.out.println("We are playing cricket");
				break;
			default:
				System.out.println("What are we playing");
				break;
		}
	}

}
